describe('Party Horn Tests', () => {
  beforeEach(() => {
    cy.visit('http://127.0.0.1:5500/Part2-Cypress/');
  });

  it('First Test', () => {
    expect(true).to.equal(true);
  });

  it('Slider changes when volume input changes', () => {
    cy.get('#volume-number').clear().type('75');
    cy.get('#volume-slider').then(function($el){
      expect($el).to.have.value(75);
    });
  });

    it('Volume input changes when slider changes', () => {
      cy.get('#volume-slider').invoke('val', 33).trigger('input');
      cy.get('#volume-number').then(function($el){
        expect($el).to.have.value(33);
      });
});


    it('Audio change when slider changes', () => {
      cy.get('#volume-slider').invoke('val', 33).trigger('input');
      cy.get('#horn-sound').then(function($el){
        expect($el).to.have.prop('volume', 0.33);
     });
    });

    it('Image and sound sources change when you select the party horn radio button', () => {
      cy.get('#radio-party-horn').invoke('val', 'checked', true).trigger('change');
      cy.get('#horn-sound').then(function($el){
        expect($el).to.have.attr('src', './assets/media/audio/party-horn.mp3');
      });
      cy.get('#sound-image').then(function($el){
        expect($el).to.have.attr('src', './assets/media/images/party-horn.svg');
      });
    });

    it('Volume image changes when increasing volumes', () => {
      //lvl 3
      cy.get('#volume-slider').invoke('val', 90).trigger('input');
      cy.get('#volume-image').then(function($el){
        expect($el).to.have.attr('src', './assets/media/icons/volume-level-3.svg');
      });
      //lvl 2
      cy.get('#volume-slider').invoke('val', 65).trigger('input');
      cy.get('#volume-image').then(function($el){
        expect($el).to.have.attr('src', './assets/media/icons/volume-level-2.svg');
      });
      //lvl 1
      cy.get('#volume-slider').invoke('val', 32).trigger('input');
      cy.get('#volume-image').then(function($el){
        expect($el).to.have.attr('src', './assets/media/icons/volume-level-1.svg');
      });
      //lvl 0
      cy.get('#volume-slider').invoke('val', 0).trigger('input');
      cy.get('#volume-image').then(function($el){
        expect($el).to.have.attr('src', './assets/media/icons/volume-level-0.svg');
      });

    });
    it('honk button is disabled when the textbox input is a empty or a non-number', () => {
      cy.get('#volume-number').clear();
      cy.get('#honk-btn').then(function($el){
        expect($el).to.have.prop('disabled', true);
      });

      cy.get('#volume-number').type('hello :)');
      cy.get('#honk-btn').then(function($el){
        expect($el).to.have.prop('disabled', true);
      });
    });

    it('Error is shown when you type a number outside of the given range for the volume textbox input', () => {
      cy.get('#volume-number').clear().type('4563');
      cy.get('#honk-btn').trigger('submit');
      cy.get('input:invalid').should('have.length', 1);
    })
});
